// App.js
import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import "./App.css";
import { Container, Row, Col } from "react-bootstrap";
import ProfileSidebar from "./Components/ProfileSidebar";
import Topmenu from "./Components/Topmenu";
import Navbar from "./Components/Navbar";
import About from "./Screens/About";
import Resume from "./Screens/Resume";
import Works from "./Screens/Works";
import Contact from "./Screens/Contact";

import ProjectsScreen from "../src/Screens/ProjectsScreen";

import ScrollToTop from "./Components/ScrollToTop";
import ScrollToTopButton from "./Components/ScrollToTopButton";

function App() {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode((prevDarkMode) => !prevDarkMode);
  };

  return (
    <>
      <style>
        {`
        .content{
          padding-left: 15px;
          padding-right: 15px;
          margin-top: 8%;
        }

        @media (max-width: 767.98px){
          .content{
            padding-left: 0;
            padding-right: 0;
            margin-top: 160px;
          }
        }

        @media (min-width: 768px) and (max-width: 991.98px){
          .content{
            padding-left: 10%;
            padding-right: 10%;
          }
          .pages{
            margin-top: 2em !important;
          }
          .content{
            margin-top: 160px;
          }
        }
        
        @media (min-width: 991.98px) and (max-width: 1399px) {
          .content{
            margin-top: 225px;
          }
        } 
      `}
      </style>
      <Router>
        <Container
          fluid
          className={`background-image-container ${
            darkMode ? "dark-mode" : ""
          }`}
        >
          <Row>
            <Topmenu darkMode={darkMode} toggleDarkMode={toggleDarkMode} />
          </Row>
          <ScrollToTop />
          <ScrollToTopButton />
          <Row className="content pb-5">
            <Col sm={12} md={12} lg={4} xl={4} xxl={3}>
              <ProfileSidebar />
            </Col>
            {/* ====================== */}
            <Col
              sm={12}
              md={12}
              lg={8}
              xl={8}
              xxl={8}
              className="my-5 my-md-0 my-lg-0 pages rounded-3"
              style={{ backgroundColor: "var(--primary-color)" }}
            >
              <Routes>
                <Route path="/" element={<About />} />
                <Route path="/about" element={<About />} />
                <Route path="/resume" element={<Resume />} />
                <Route path="/works" element={<Works />} />
                <Route path="/contact" element={<Contact />} />
                <Route
                  path="/projects/:projectId"
                  element={<ProjectsScreen />}
                />{" "}
                {/* Use ProjectsScreen component here */}
              </Routes>
            </Col>
            {/* ====================== */}
            <Col sm={12} md={12} xl={1}>
              <Navbar />
            </Col>
          </Row>
        </Container>
      </Router>
    </>
  );
}

export default App;
