import React, { useState } from "react";
import { Col, Row, Button } from "react-bootstrap";
import ProjectsCard from "../Components/ProjectsCard";
import Copyright from "../Components/copyright";
import "../App.css";
import projectDesktopAPI from "../Components/api/projectDesktopAPI";

// Mock data for demonstration (Replace this with your actual data)
const gameProjects = projectDesktopAPI.filter(
  (project) => project.category === "game"
);
const websiteProjects = projectDesktopAPI.filter(
  (project) => project.category === "website"
);

const Works = () => {
  const [activeTab, setActiveTab] = useState("games"); // Default tab

  return (
    <>
      <style>
        {`
          .tab-button {
            margin: 0 10px;
            font-size: 1.2rem;
            display: flex;
            align-items: center;
            gap: 5px;
          }
          .tab-button.active {
            background-color: var(--primary-color);
            color: #fff;
          }
        `}
      </style>
      <div
        className="rounded-3 p-4 p-md-5 p-lg-5"
        style={{
          color: "var(--third-color)",
        }}
      >
        <h1 className="page-title resp-h1 text-center">Works</h1>

        {/* Tab Buttons */}
        <div className="d-flex justify-content-center mt-4">
          <Button
            className={`tab-button ${activeTab === "games" ? "active" : ""}`}
            onClick={() => setActiveTab("games")}
          >
            <i className="bi bi-controller"></i> {/* Bootstrap Icon */}
            Games
          </Button>
          <Button
            className={`tab-button ${activeTab === "websites" ? "active" : ""}`}
            onClick={() => setActiveTab("websites")}
          >
            <i className="bi bi-window"></i> {/* Bootstrap Icon */}
            Websites
          </Button>
        </div>

        {/* Content */}
        <Row className="mt-3 mt-md-5">
          {(activeTab === "games" ? gameProjects : websiteProjects).map(
            (project) => (
              <Col
                key={project.id}
                sm={12}
                md={6}
                lg={4}
                xl={4}
                className="p-2"
              >
                <ProjectsCard project={project} />
              </Col>
            )
          )}
        </Row>

        <Row className="mt-5 d-flex">
          <Copyright />
        </Row>
      </div>
    </>
  );
};

export default Works;
